package hash

import (
	"testing"

	"github.com/eaigner/igi/trinary"
)

const (
	trytes = ""
	hash   = "TIXEPIEYMGURTQ9ABVYVQSWMNGCVQFASMFAEQWUZCLIWLCDIGYVXOEJBBEMZOIHAYSUQMEFOGZBXUMHQW"
)

func TestCurlP81(t *testing.T) {
	var in [10 * 1024]int8
	var out [CurlHashLength]int8
	var curl Curl
	var sponge Sponge = &curl // to test interface conformance

	n, err := trinary.TritsFromTrytes(in[:], trytes)
	if err != nil {
		t.Fatal(err)
	}
	if n != 8019 {
		t.Fatal(n)
	}

	sponge.Reset(CurlP81)
	sponge.Absorb(in[:n])
	sponge.Squeeze(out[:])

	s, err := trinary.Trytes(out[:])
	if err != nil {
		t.Fatal(err)
	}
	if s != hash {
		t.Logf("is:   %s", s)
		t.Logf("want: %s", hash)
		t.FailNow()
	}
}

func BenchmarkCurlP81(b *testing.B) {
	var in [8019]int8
	var out [CurlHashLength]int8
	var curl Curl

	n, err := trinary.TritsFromTrytes(in[:], trytes)
	if err != nil {
		b.Fatal(err)
	}

	for i := 0; i < b.N; i++ {
		curl.Reset(CurlP81)
		curl.Absorb(in[:n])
		curl.Squeeze(out[:])
	}
}
